import { DatePipe, NumberSymbol } from '@angular/common';
import { Component } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { StatisticService } from 'src/app/Services/statistic.service';

@Component({
  selector: 'app-statistics',
  templateUrl: './statistics.component.html',
  styleUrls: ['./statistics.component.scss']

})
export class StatisticsComponent {

  constructor(private _servise: StatisticService) { }
  views$: Observable<number> = this._servise.views$;
  loves$: Observable<number> = this._servise.loves$;
  private mySub1 = new Subscription();
  private mySub2 = new Subscription();
  views: number;
  loves: number;
  curDate: Date;


  ngOnInit() {
    this.curDate = new Date();
    this.views$ = this._servise.views$;
    this.loves$ = this._servise.loves$;
    this.mySub1 = this._servise.views$.subscribe();
    this.mySub2 = this._servise.loves$.subscribe();
  }
}
