import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Recipe } from 'src/app/Models/recipe';
import { RecipeService } from 'src/app/Services/recipe.service';

@Component({
  selector: 'app-recipe-page',
  templateUrl: './recipe-page.component.html',
  styleUrls: ['./recipe-page.component.scss']
})
export class RecipePageComponent {
  recipeName: string;
  recipe: Recipe;
  constructor(private route: ActivatedRoute, private service:RecipeService){ }
  ngOnInit() {
    this.route.queryParamMap.subscribe(params=>{this.recipeName=params.get('name');})
    this.route.params.subscribe(params => {
      this.recipeName = params['name']; // Get the recipe ID from the route
      this.recipe = this.service.getRecipeByName(this.recipeName); // Fetch the recipe based on ID
    });
  }
}

