import { Injectable } from '@angular/core';
import { Recipe } from '../Models/recipe';

@Injectable({
  providedIn: 'root'
})
export class RecipeService {

  constructor() { }

  recipesList: Recipe[] = [];
  showAllRecipe() {
    return this.recipesList;
  }

  addRecipe(recipe: Recipe) {
    this.recipesList.push(recipe);
  }

  recipes: Recipe[] = [{
    ingrediens: ['biscuits','sugar', 'cocoa', 'vanila', 'oil', 'water', 'coconut', 'sprinkles'],
    Instructions: ['crush the biscuits',
   'heat the rest of the ingredients in a pot on the fire' ,
    'add the biscuits',
    'Make balls and decorate'],
    Difficulty: 1.5
  },
  {
    ingrediens: ['potatos','carrots', 'onions', 'garlic', 'oil', 'water', 'salt', 'black paper'],
    Instructions: ['crush the biscuits',
   'heat the rest of the ingredients in a pot on the fire' ,
    'add the biscuits',
    'Make balls and decorate'],
    Difficulty: 1.5
  }
  ];
}
