import { DOCUMENT } from '@angular/common';
import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Recipe } from 'src/app/Models/recipe';

@Component({
  selector: 'app-add-recipe',
  templateUrl: './add-recipe.component.html',
  styleUrls: ['./add-recipe.component.scss']
})

export class AddRecipeComponent {
  forms: FormGroup;
// myRecipe:Recipe=new;

  constructor(private formBuilder: FormBuilder) {
    this.forms = this.formBuilder.group({
      'name': new FormControl('enter recipe name', [Validators.required,Validators.maxLength(10)]),
      'newIngred': new FormControl('enter ingredient', [Validators.required,Validators.maxLength(10)]),
      'instructions': new FormControl('enter instruction', [Validators.required,Validators.minLength(10)])
    });
  }

  x:boolean=false
  formSubmitted() {
    if (!this.forms.valid)
      return;
    console.log(this.forms);
    this.x=true
  }
//רשמת המוצרים
  newIngred:string="";
  allIngrediens:string[]=[];
  addIngrediens(){
    this.allIngrediens.push(this.newIngred);
    //Reset input
    this.newIngred ="";
    this.allIngrediens.push(<HTMLInputElement>document.getElementById("newIngred")).value);

  }
//רשימת הוראות
  newInstruction:string="";
  allInstructions:string[]=[];
  addIinstructions(){
    this.allInstructions.push(this.newIngred);

    this.newIngred ="";
  }
}
