import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Recipe } from 'src/app/Models/recipe';
import { RecipeService } from 'src/app/Services/recipe.service';

@Component({
  selector: 'app-recipe-page',
  templateUrl: './recipe-page.component.html',
  styleUrls: ['./recipe-page.component.scss']
})
export class RecipePageComponent {
  recipe: Recipe=null;
  constructor(private route: ActivatedRoute, private service:RecipeService){ }
  ngOnInit() {
    this.route.params.subscribe(params => {
      let recipeName = params['name'];
      this.recipe = this.service.getRecipeByName(recipeName); 

    });
  }
  ingrediens:string[]=this.recipe.ingrediens;
  instructions:string[]=this.recipe.ingrediens;
}
<div id="body">

<h1>name: {{recipe.name}}</h1>
<h4>level: {{recipe.Difficulty}}</h4>
<ul id="all" *ngFor="let r of ingrediens">
<li>r</li>
</ul>
<ul id="alls" *ngFor="let i of instructions">
    <li>i</li>
    </ul>
<h4>is your favorite: {{recipe.isFavorite}}</h4>
</div>
