ngOnInit(){
  if(this.isLiked===true)
  this.isLiked=false;
  else
  this.isLiked=true;
  }

  toggleLike(){
    this.isLiked=!this.isLiked;
    this.changeLikeEvevnt.emit(this.isLiked)
  }
}
