import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-recipe',
  templateUrl: './add-recipe.component.html',
  styleUrls: ['./add-recipe.component.scss']
})

export class AddRecipeComponent {
  form: FormGroup;
  constructor(private formBuilder: FormBuilder) {
    this.form = this.formBuilder.group({
      'name': new FormControl('enter your name', [Validators.maxLength(10)]),

    });
  }
<form [formGroup]="form" (ngSubmit)="formSubmitted()">
    <div>
        <!-- שם -->
        <label>name:</label><input type="text" formControlName="name"
          [ngClass]="this.form.get('name')?.touched && !this.form.get('name')?.valid?'error-message':''" >
       <div *ngIf="this.form.get('name').touched && this.form.get('name').hasError('required')">שדה חובה</div>
        <div *ngIf="this.form.get('name').hasError('maxlength')">עד 10 תווים</div> 

    </div>

   <!-- הצגת שלח -->

<button type="submit" [disabled]="!form.valid">שלח</button>
<div *ngIf="x">t {{this.form.get('name')?.value}}, You have successfully registered</div> 
</form>
