import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { RecipeService } from './recipe.service';

@Injectable({
  providedIn: 'root'
})
export class StatisticService {

  constructor(private recipeService: RecipeService) { 
    this.firstLovedFunc();
  }

  views: BehaviorSubject<number> = new BehaviorSubject<number>(13);
  get views$(): Observable<number> {
    return this.views.asObservable();
  }
  firstLoves: number;
  firstLovedFunc() {
    this.firstLoves = this.recipeService.firstLove();
    this.loves = new BehaviorSubject<number>(this.firstLoves);
  }
  loves: BehaviorSubject<number> = null
  get loves$(): Observable<number> {

    return this.loves.asObservable();
  }

  addViews() {
    this.views.next(1);
  }
  addLove() {
    this.loves.next(this.loves.getValue);
  }
  lessLove() {
    this.loves.next(-1);
  }
}
