import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class StatisticService {
  //views:number=0;
  favourites:number=0;
  constructor() { }

  views: BehaviorSubject<number> = new BehaviorSubject<number>(0);
  get Recipes$(): Observable<number> {
    return this.views.asObservable();
  }
}
