import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-add-recipe',
  templateUrl: './add-recipe.component.html',
  styleUrls: ['./add-recipe.component.scss']
})
form: FormGroup;
export class AddRecipeComponent {
  constructor(private formBuilder: FormBuilder) {
    this.form = this.formBuilder.group({
      'name': new FormControl('enter your name', [ Validators.maxLength(10)]),
      'email': new FormControl('enter your email', [Validators.required, Validators.email]),
      'age': new FormControl('enter your age', [Validators.required,Validators.min(18)]),
      'password': new FormControl('enter your password', [ Validators.minLength(8)])
    });
}
