import { Injectable } from '@angular/core';
import { Recipe } from '../Models/recipe';

@Injectable({
  providedIn: 'root'
})
export class RecipeService {

  constructor() { }

  recipesList: Recipe[] = [];
  showAllRecipe() {
    return this.recipesList;
  }

  addRecipe(recipe: Recipe) {
    this.recipesList.push(recipe);
  }

  recipes: Recipe[] = [{
    ingrediens: ['biscuits','sugar', 'cocoa', 'vanila', 'oil', 'water', 'coconut'],
    Instructions: ['put all ingridiens', 'mix', 'cake'],
    Difficulty: 4
  }
  ];
}
