import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable, Subscription } from 'rxjs';
import { Recipe } from 'src/app/Models/recipe';
import { RecipeService } from 'src/app/Services/recipe.service';
import { StatisticService } from 'src/app/Services/statistic.service';

@Component({
  selector: 'app-recipes',
  templateUrl: './recipes.component.html',
  styleUrls: ['./recipes.component.scss']
})
export class RecipesComponent {
  isFavoritPage: boolean = false;
  x: string = "";
  favorit: boolean = true;
  isFav: string="0";
name:string;
  private sub = new Subscription();
  private sub2 = new Subscription();
  private sub3 = new Subscription();
  constructor(private recipeServise: RecipeService, private myRoute: ActivatedRoute, private statisticService:StatisticService) { }

  //לא אמור להיות!!!!!!!!!!!
  //!!!!!!!
  recipes$: Observable<Recipe[]> = this.recipeServise.Recipes$;
  views$: Observable<number> = this.statisticService.views$;
  loves$: Observable<number> = this.statisticService.loves$;
  //עם אתחול
  ngOnInit() {
    this.recipes$ = this.recipeServise.Recipes$;
    this.sub = this.recipeServise.Recipes$.subscribe();
    this.sub2 = this.statisticService.views$.subscribe();
    this.sub3 = this.statisticService.loves$.subscribe();
    this.myRoute.queryParamMap.subscribe(params => { this.isFav = params.get('isF'); })

  }
  // returnRecipe(){
  //   let y = this.recipeServise.showAllRecipe()
  //   for (let i = 0; i < y.length; i++) {
  //     if (y[i].name === this.x)
  //     return y[i];    
  //   }
  //   return null;
  // }
  DeleteRecipe(r: Recipe) {
    if (!confirm("are you shur?"))
    return;
      this.recipeServise.DeleteRecipe(r);
  }

  changFavority(r: Recipe) {
    if (r.isFavorite === false){
      r.isFavorite = true;
     //this.statisticService.loves$;
    }
    else{
      r.isFavorite = false;
      // this.statisticService.favourites--;
    }
  }
}
