import { Component } from '@angular/core';
import { elementAt } from 'rxjs';
import { Recipe } from 'src/app/Models/recipe';
import { RecipeService } from 'src/app/Services/recipe.service';

@Component({
  selector: 'app-recipes',
  templateUrl: './recipes.component.html',
  styleUrls: ['./recipes.component.scss']
})
export class RecipesComponent {
  isFavorit: boolean = false;
  constructor(private recipeServise: RecipeService) { }

  //  ngOnInit(){
  //   if(this.isFavorit==true){

  //   }
  //  }


  recipes = this.recipeServise.showAllRecipe();
  keepName(name: string) {
    let x = name;
    let y = this.recipeServise.showAllRecipe().forEach(elementAt => {
      if (element.name === x)
        return element;
    });
  }


}
